// weather-icons.helper.ts
export function getWeatherIconUrl(condition) {
  var conditionMap = {
    'Clear': 'sun.png',
    'Clouds': 'clouds.png',
    'Rain': 'rain.png',
    'Snow': 'snow.png' // Add more conditions and corresponding icons as needed

  };
  var defaultIcon = 'default.png';
  var iconFilename = conditionMap[condition] || defaultIcon;
  return "https://www.angulartraining.com/images/weather/".concat(iconFilename);
}
